mapper.select:
SELECT h.id AS hotelId, h.name AS hotelName, COUNT(o.id) AS totalBookings, COUNT(CASE WHEN o.status = 'confirmed' THEN 1 END) AS confirmedBookings, COUNT(CASE WHEN o.status = 'checked_in' THEN 1 END) AS checkInCount, COUNT(CASE WHEN o.status = 'cancelled' THEN 1 END) AS cancelledBookings, (COUNT(CASE WHEN o.status IN ('confirmed', 'checked_in', 'checked_out') THEN 1 END) * 100.0 / COUNT(o.id)) AS bookingRate, (COUNT(CASE WHEN o.status = 'checked_in' THEN 1 END) * 100.0 / COUNT(o.id)) AS checkInRate FROM hotels h LEFT JOIN rooms r ON h.id = r.hotel_id LEFT JOIN orders o ON r.id = o.room_id AND o.created_at >= #{startDate} AND o.created_at <= #{endDate} WHERE 1=1
AND h.id = #{hotelId}
GROUP BY h.id, h.name ORDER BY h.id

SELECT h.id AS hotelId, h.name AS hotelName, DATE_FORMAT(o.created_at, '%Y-%m') AS month, SUM(o.total_amount) AS totalRevenue, AVG(o.total_amount) AS averageRoomPrice, COUNT(o.id) AS orderCount FROM hotels h LEFT JOIN rooms r ON h.id = r.hotel_id LEFT JOIN orders o ON r.id = o.room_id AND o.created_at >= #{startDate} AND o.created_at <= #{endDate} AND o.status != 'cancelled' WHERE 1=1
AND h.id = #{hotelId}
GROUP BY h.id, h.name, DATE_FORMAT(o.created_at, '%Y-%m') ORDER BY h.id, month

SELECT h.id AS hotelId, h.name AS hotelName, DATE(o.check_in_date) AS date, COUNT(r.id) AS totalRooms, COUNT(CASE WHEN o.status IN ('checked_in', 'checked_out') THEN 1 END) AS occupiedRooms, (COUNT(CASE WHEN o.status IN ('checked_in', 'checked_out') THEN 1 END) * 100.0 / COUNT(r.id)) AS occupancyRate FROM hotels h LEFT JOIN rooms r ON h.id = r.hotel_id LEFT JOIN orders o ON r.id = o.room_id AND o.check_in_date >= #{startDate} AND o.check_in_date <= #{endDate} WHERE 1=1
AND h.id = #{hotelId}
GROUP BY h.id, h.name, DATE(o.check_in_date) ORDER BY h.id, date
